mapper.select:
select no ,grpno ,depth ,userid ,title ,content ,time ,status FROM inquiry_tb WHERE depth = 0 AND deleted=0
AND userid = #{userId}
ORDER BY no DESC

mapper.update:
UPDATE inquiry_tb
set deleted = 1
WHERE no = #{no}

UPDATE inquiry_tb
SET title = #{title}
,content = #{content}
,time = NOW()
WHERE no= #{no}

Update inquiry_tb
set status = #{status}
WHERE no= #{grpNo}
