mapper.select:
select no ,grpno ,depth ,userid ,title ,content ,time ,status FROM inquiry_tb WHERE depth = 0
AND userid = #{userId}
ORDER BY no DESC

SELECT * FROM inquiry_tb
WHERE no= #{no} OR grpno= #{no}
ORDER BY depth ASC

mapper.update:
UPDATE inquiry_tb
SET title = #{title}
    ,content = #{content}
    ,time = NOW()
WHERE no= #{no}
